// SPDX-License-Identifier: MIT
pragma solidity >=0.7.4;
pragma abicoder v2;

interface ITriangularArbitrageOnUniswapV3 {
    /// @notice 币对数据结构体
    struct PoolPairOuput {
        uint160 sqrtPriceX96;
        int24 tick;
        int24 tickSpace;
        PopulatedTick[] liquidties;
    }
    /// @notice tick 的流动性
    struct PopulatedTick {
        int24 tick;
        int128 liquidityNet;
        uint128 liquidityGross;
    }

    /// @notice 添加一个白名单地址
    /// @param whiteAddress 需要添加的白名单地址
    function addWhiteAddress(address whiteAddress) external;

    /// @notice 移除一个白名单
    /// @param whiteAddress 需要删除的地址
    function removeWhiteAddress(address whiteAddress) external;

    /// @notice 清空白名单
    function clearWhiteAddress() external;

    /// @notice 获取当前白名单地址列表
    /// @param checker 需要检查的地址
    /// @return 结果，true 是白名单，false 不是白名单
    function isWhiteAddress(address checker) external view returns (bool);

    /// @notice 充值
    /// @param token 要充值的代币 token
    /// @param amount 要充值的代币金额
    function deposit(address token, uint160 amount) external;

    /// @notice 提现
    /// @param token 要提现的代币 token
    /// @param to 提现的目标地址
    /// @param amount 要提现的代币金额
    function withdraw(
        address token,
        address to,
        uint160 amount
    ) external;

    /// @notice 获取特定代币的存储数量
    /// @param token 查询的代币
    /// @return 当前代币存储的金额
    function getAccountBalance(address token) external view returns (uint256);

    /// @notice 获取特定代币的所有余额，只有 owner 可以使用
    /// @param token  获取 token 代币
    /// @return 代币总的余额
    function getAllBalance(address token) external view returns (uint256);

    /// @notice 将特定代币的所有余额都提现到 owner 账户，只有 owner 可以使用
    /// @param token token 代币
    /// @return 本地提币的数量
    function withdrawAll(address token) external returns (uint256);

    /// @notice 获取多个币对的数据
    /// @param poolAddress uniswap pool 地址
    /// @param tickSapcingOffset 获取深度的偏移量，返回的结果最多是 tick spacing offset * 2 + 1 个
    /// @return prices 价格列表[{sqrtPriceX96, tick}]，顺序和 pool 放入的顺序相同，liquidties 的顺序是价格从低到高排序
    function getPairTicks(
        address[] calldata poolAddress,
        uint16 tickSapcingOffset
    ) external view returns (PoolPairOuput[] memory prices);

    /// @notice 根据填入的poolAddresList，来进行套利，poolAddresList 必须是一个闭环,A -> B -> ... -> A，顺序代表了交易路径
    /// @param token tokenA 的合约地址
    /// @param poolAddressList pool 地址，必须和 tokens 匹配
    /// @param amount 本次需要借贷/交易的金额
    /// @param minProfit 最小收益金额，单位是 token list 第一个 币的单位
    function arbitrage(
        address token,
        address[] calldata poolAddressList,
        uint160 amount,
        uint160 minProfit
    ) external;

    /// @notice 套利成功发送的事件
    /// @param sender 套利人地址
    /// @param profitToken 收益的币种合约地址
    /// @param amount 本次发生的起始金额
    /// @param profitAmount 本次收益的金额
    event Arbitrage(
        address indexed sender,
        address indexed profitToken,
        uint256 amount,
        uint256 profitAmount
    );

    /// @notice 添加成功一个白名单事件
    /// @param whiteAddress 白名单地址
    event AddWhiteAddress(address indexed whiteAddress);
    /// @notice 添加成功一个白名单事件
    /// @param whiteAddress 白名单地址
    event RemoveWhiteAddress(address indexed whiteAddress);
    /// @notice amountSpecified
    event ClearWhiteAddress();

    /// @notice 充值事件
    /// @param sender 充值的人
    event Deposit(
        address indexed sender,
        address indexed token,
        uint256 amount
    );

    /// da'yin
    event PrintSwap(
        address fromAddress,
        address toAddress,
        address poolAddress,
        uint256 amount,
        uint160 sqrtPriceLimitX96
    );
}

