pragma solidity >=0.7.6;

import "../interfaces/IUniswapV3Factory.sol";
import "../interfaces/IUniswapV3Pool.sol";

import "./PeripheryImmutableState.sol";
import "../interfaces/IPoolInitializer.sol";

/// @title Creates and initializes V3 Pools
abstract contract PoolInitializer is IPoolInitializer, PeripheryImmutableState {
    /// @inheritdoc IPoolInitializer
    // 第一次调用，主要用于初始化 池子，并设置初始价格
    function createAndInitializePoolIfNecessary(
        address token0,
        address token1,
        uint24 fee,
        uint160 sqrtPriceX96
    ) external payable override returns (address poolAddress) {
        require(token0 < token1);
        // 获取 已经存在的池子
        poolAddress = IUniswapV3Factory(factory).getPool(token0, token1, fee);
        // 如果不存在
        if (poolAddress == address(0)) {
            // 创建一个池子
            poolAddress = IUniswapV3Factory(factory).createPool(
                token0,
                token1,
                fee
            );
            IUniswapV3Pool(poolAddress).initialize(sqrtPriceX96);
            //初始化价格
        } else {
            IUniswapV3Pool pool = IUniswapV3Pool(poolAddress);
            // 存在，则初始化价格
            (uint160 sqrtPriceX96Existing, , , , , , ) = pool.slot0();
            if (sqrtPriceX96Existing == 0) {
                pool.initialize(sqrtPriceX96);
            }
        }
    }
}
